VIRTUALSLIDESDIR="/hpc/dhl_ec/VirtualSlides"

# Here we copy for a given stain the slides from the bulk directory to the virtual slides directory
# Stains are:
    # CD14
    # CD3
    # CD31
    # CD34
    # CD3_CD56_NKT
    # CD42B
    # CD66b
    # CD68
    # CD8
    # EVG
    # FIBRIN
    # GLYCC
    # HE
    # HE-FIBRIN
    # HHIPL1
    # MPO
    # MT
    # SMA
    # SR
    # SR_POLARIZED
    # VONWILLEBRANDFACTOR

STAIN="GLYCC"

for STUDYNUMBER in ${AE_list2461}; do

    echo "> Copying slides for study ${STUDYNUMBER} and stain ${STAIN}"
    cp -v ${BULKDIR}/${STAIN}/AE${STUDYNUMBER}.* ${VIRTUALSLIDESDIR}/${STAIN}/

done

